import React from 'react';
import PropTypes from 'prop-types';
import classNames from 'classnames';
import { mapToCssModules } from '../utils';

const CarouselCaption = (props) => {
  const { captionHeader, captionText, cssModule, className } = props;
  const classes = mapToCssModules(
    classNames(className, 'carousel-caption', 'd-md-block'),
    cssModule,
  );

  const captionHeaderClasess = mapToCssModules(
    classNames('carousel-caption-header', 'fw-bold'),
    cssModule,
  );

  const captionTextClasess = mapToCssModules(
    classNames('carousel-caption-text', 'fw-normal'),
    cssModule,
  );

  return (
    <div className={classes}>
      <span className={captionHeaderClasess}>{captionHeader}</span>
      <p className={captionTextClasess}>{captionText}</p>
    </div>
  );
};

CarouselCaption.propTypes = {
  captionHeader: PropTypes.node,
  captionText: PropTypes.node.isRequired,
  cssModule: PropTypes.object,
  className: PropTypes.string,
};

export default CarouselCaption;
